import React, { useState, useEffect } from 'react';
import '../carousel/Carousel.css';

const Carousel = ({ images }) => {
  const [currentIndex, setCurrentIndex] = useState(0);  // Começa com o primeiro índice

  // Função para mudar a imagem do carrossel
  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 3) % images.length); // Avança de 3 em 3
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 3 + images.length) % images.length); // Retrocede de 3 em 3
  };

  // Muda automaticamente a imagem a cada 3 segundos
  useEffect(() => {
    const interval = setInterval(nextSlide, 3000);
    return () => clearInterval(interval); // Limpa o intervalo quando o componente for desmontado
  }, []);

  return (
    <div className="carousel">
      <div className="carousel-images" style={{ transform: `translateX(-${(currentIndex * 33.33)}%)` }}>
        {images.map((image, index) => (
          <img key={index} src={image} alt={`Foto ${index + 1}`} className="carousel-image" />
        ))}
      </div>
      <div className="carousel-buttons">
        <button className="button" onClick={prevSlide}>❮</button>
        <button className="button" onClick={nextSlide}>❯</button>
      </div>
    </div>
  );
};

export default Carousel;
