import React, { useState, useEffect } from 'react';

// Importação das imagens
import foto1 from '../../assets/foto1.jpg';
import foto2 from '../../assets/foto2.jpg';
import foto3 from '../../assets/foto3.jpg';
import foto4 from '../../assets/foto4.jpg';
import foto5 from '../../assets/foto5.jpg';
import foto6 from '../../assets/foto6.jpg';
import foto7 from '../../assets/foto7.jpg';
import foto8 from '../../assets/foto8.jpg';

import '../home/Home.css'; // Certifique-se de que o caminho esteja correto

const carouselImages = [foto1, foto2, foto3, foto4, foto5, foto6, foto7, foto8];

const Home = () => {
  const [currentIndex, setCurrentIndex] = useState(1);  // Começar com o primeiro índice real (1, não 0)

  // Função para mudar a imagem do carrossel
  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % carouselImages.length); // Ajustado para usar o comprimento correto
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + carouselImages.length) % carouselImages.length); // Ajustado para usar o comprimento correto
  };

  // Muda automaticamente a imagem a cada 3 segundos
  useEffect(() => {
    const interval = setInterval(nextSlide, 3000);
    return () => clearInterval(interval); // Limpa o intervalo quando o componente for desmontado
  }, []);

  return (
    <div>
      {/* Carrossel de Fotos */}
      <div className="carousel">
        <div className="carousel-images" id="carousel-images">
          <img src={carouselImages[currentIndex]} alt={`Foto ${currentIndex}`} className="carousel-image" />
        </div>
        <div className="carousel-buttons">
          <button className="button" id="prev" onClick={prevSlide}>❮</button>
          <button className="button" id="next" onClick={nextSlide}>❯</button>
        </div>
      </div>

      <main>
        <section className="photo-text-sections">
          <div className="section-item">
            <img src={foto1} alt="Foto 1" className="section-image" />
            <div className="section-content">
              <img src="img/bandeirinha.png" alt="Cordão de Luz" className="section-decoration1" />
              <h2>Bem-Vindo a Festa do Pijama Petrópolis</h2>
              <p>Realizar uma festa do pijama é trazer magia à vida real! Além de super divertida, é uma oportunidade única para criar laços, fortalecer amizades e proporcionar momentos de alegria que ficarão na memória para sempre. Essa experiência estimula a criatividade das crianças e será lembrada com carinho por todos!</p>
            </div>
          </div>
          {/* Outras seções... */}
        </section>
      </main>
    </div>
  );
};

export default Home;
