import React, { useState, useEffect } from 'react';
import '../carousel/Carousel.css';

const Carousel = ({ images }) => {
  const [currentIndex, setCurrentIndex] = useState(0);  // Começa com o primeiro índice

  // Função para mudar a imagem do carrossel
  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length); // Ajustado para usar o comprimento correto
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length); // Ajustado para usar o comprimento correto
  };

  // Muda automaticamente a imagem a cada 3 segundos
  useEffect(() => {
    const interval = setInterval(nextSlide, 3000);
    return () => clearInterval(interval); // Limpa o intervalo quando o componente for desmontado
  }, []);

  return (
    <div className="carousel">
      <div className="carousel-images" id="carousel-images">
        <img src={images[currentIndex]} alt={`Foto ${currentIndex + 1}`} className="carousel-image" />
      </div>
      <div className="carousel-buttons">
        <button className="button" id="prev" onClick={prevSlide}>❮</button>
        <button className="button" id="next" onClick={nextSlide}>❯</button>
      </div>
    </div>
  );
};

export default Carousel;
